mapper.select:
        SELECT CAST(mf.id AS CHAR) AS "id",mf.name AS "name",mf.original_filename AS "originalFilename",IFNULL(mf.file_size/1048576, "-") AS "fileSize",mf.file_size AS "originFileSize",IFNULL(mf.content_type, "-") AS "contentType",mf.content_type AS "originContentType",mf.file_type AS "fileType",mf.url AS "url",mf.type AS "type",CAST(mf.parent_id AS CHAR) AS "parentId",mf.create_time AS "createTime",mf.tenant_code AS "tenantCode",IFNULL(msd.dict_name, "-") AS "fileTypeCn"
        FROM mscode_file mf LEFT JOIN mscode_sys_dict msd ON mf.file_type = msd.dict_value AND msd.dict_type = 'file'
        WHERE 1 = 1 

            AND mf.original_filename LIKE CONCAT('%',#{originalFilename},'%')


            AND mf.content LIKE CONCAT('%',#{content},'%')


            AND mf.tenant_code = #{tenantCode}


            AND mf.parent_id = #{id}


            AND mf.id IN

	            #{id,jdbcType=BIGINT}



            AND mf.parent_id = #{parentId}



        		AND mf.parent_id = (SELECT mscode_file.parent_id FROM mscode_file WHERE mscode_file.id = #{previousId})


        		AND mf.parent_id = 0



            ORDER BY mf.type ASC,${sort} ${sequence}
