mapper.sql:
SELECT 
FROM 

    is_deleted = 0

        AND mod_id = ${reports.modId}



        AND mod_name LIKE CONCAT('%',#{reports.modName},'%')



        AND rpt_name LIKE CONCAT('%',#{reports.rptName},'%')



        AND rpt_path LIKE CONCAT('%',#{reports.rptPath},'%')



        AND rpt_file_name LIKE CONCAT('%',#{reports.rptFileName},'%')



        AND para_num = ${reports.paraNum}



        AND proc_name LIKE CONCAT('%',#{reports.procName},'%')



        AND direction_flag LIKE CONCAT('%',#{reports.directionFlag},'%')



        AND is_child_flag LIKE CONCAT('%',#{reports.isChildFlag},'%')



        AND para_str LIKE CONCAT('%',#{reports.paraStr},'%')



        AND menu_id = ${reports.menuId}



        AND menu_code LIKE CONCAT('%',#{reports.menuCode},'%')



        AND remark LIKE CONCAT('%',#{reports.remark},'%')



        AND org_id = ${reports.orgId}



        AND tenant_id = ${reports.tenantId}



        AND tenant_code LIKE CONCAT('%',#{reports.tenantCode},'%')



        AND create_user_id = ${reports.createUserId}



        AND update_id = ${reports.updateId}



        AND deleted_user_id = ${reports.deletedUserId}



        AND delete_user_name LIKE CONCAT('%',#{reports.deleteUserName},'%')



        AND delete_time = ${reports.deleteTime}

mapper.select:
SELECT id 'id', mod_id 'modId', mod_name 'modName', rpt_name 'rptName', rpt_path 'rptPath', rpt_file_name 'rptFileName', para_num 'paraNum', proc_name 'procName', direction_flag 'directionFlag', is_child_flag 'isChildFlag', para_str 'paraStr', menu_id 'menuId', menu_code 'menuCode', remark 'remark', org_id 'orgId', tenant_id 'tenantId', tenant_code 'tenantCode', create_user_id 'createUserId', create_by 'createBy', create_time 'createTime', update_id 'updateId', update_by 'updateBy', update_time 'updateTime', is_deleted 'isDeleted', deleted_user_id 'deletedUserId', delete_user_name 'deleteUserName', delete_time 'deleteTime' FROM reports WHERE is_deleted = 0
AND mod_id = #{modId}
AND mod_name LIKE CONCAT('%',#{modName}, '%')
AND rpt_name LIKE CONCAT('%',#{rptName}, '%')
AND rpt_path LIKE CONCAT('%',#{rptPath}, '%')
AND rpt_file_name LIKE CONCAT('%',#{rptFileName}, '%')
AND para_num = #{paraNum}
AND proc_name LIKE CONCAT('%',#{procName}, '%')
AND direction_flag LIKE CONCAT('%',#{directionFlag}, '%')
AND is_child_flag LIKE CONCAT('%',#{isChildFlag}, '%')
AND para_str LIKE CONCAT('%',#{paraStr}, '%')
AND menu_id = #{menuId}
AND menu_code LIKE CONCAT('%',#{menuCode}, '%')
AND remark LIKE CONCAT('%',#{remark}, '%')
AND org_id = #{orgId}
AND tenant_id = #{tenantId}
AND tenant_code LIKE CONCAT('%',#{tenantCode}, '%')
AND create_user_id = #{createUserId}
AND update_id = #{updateId}
AND deleted_user_id = #{deletedUserId}
AND delete_user_name LIKE CONCAT('%',#{deleteUserName}, '%')
AND delete_time = #{deleteTime}
