SELECT id, name, password, user_name, en_user_name, number_name, qq_number, sex, phone, cell_phone_number, nailing_number, wechat_number, email, is_depart_charge, depart_id, department_list, depart_ids, rel_tenant_ids, post_id, entry_time, job_status, STATUS, user_type, supervisor_id, part_time_dep_id, code, remark, is_deleted, create_by, create_time, update_by, update_time FROM sys_user
is_deleted = 0
AND name = #{sysUser.name}
AND user_name LIKE CONCAT('%',#{sysUser.userName}, '%')
AND depart_id = #{sysUser.departId}
