mapper.sql:
SELECT 
FROM 

    voided = 0

        AND code_no LIKE CONCAT('%',#{bCountry.codeNo},'%')



        AND name_ch LIKE CONCAT('%',#{bCountry.nameCh},'%')



        AND name_en LIKE CONCAT('%',#{bCountry.nameEn},'%')



        AND hs_code LIKE CONCAT('%',#{bCountry.hsCode},'%')



        AND favored_f = ${bCountry.favoredF}



        AND affected_f = ${bCountry.affectedF}



        AND code_inter LIKE CONCAT('%',#{bCountry.codeInter},'%')



        AND order_num = ${bCountry.orderNum}



        AND remark LIKE CONCAT('%',#{bCountry.remark},'%')



        AND crt_by = ${bCountry.crtBy}



        AND crt_by_name LIKE CONCAT('%',#{bCountry.crtByName},'%')



        AND crt_by_dtm = ${bCountry.crtByDtm}



        AND mdy_by = ${bCountry.mdyBy}



        AND mdy_by_name LIKE CONCAT('%',#{bCountry.mdyByName},'%')



        AND mdy_by_dtm = ${bCountry.mdyByDtm}



        AND voided = ${bCountry.voided}



        AND voided_by = ${bCountry.voidedBy}



        AND voided_by_name LIKE CONCAT('%',#{bCountry.voidedByName},'%')



        AND voided_by_dtm = ${bCountry.voidedByDtm}



        AND three_character_code LIKE CONCAT('%',#{bCountry.threeCharacterCode},'%')

mapper.select:
SELECT id 'id', code_no 'codeNo', name_ch 'nameCh', name_en 'nameEn', hs_code 'hsCode', favored_f 'favoredF', affected_f 'affectedF', code_inter 'codeInter', order_num 'orderNum', remark 'remark', crt_by 'crtBy', crt_by_name 'crtByName', crt_by_dtm 'crtByDtm', mdy_by 'mdyBy', mdy_by_name 'mdyByName', mdy_by_dtm 'mdyByDtm', voided 'voided', voided_by 'voidedBy', voided_by_name 'voidedByName', voided_by_dtm 'voidedByDtm', three_character_code 'threeCharacterCode' FROM b_country WHERE is_deleted = 0
AND code_no LIKE CONCAT('%',#{codeNo}, '%')
AND name_ch LIKE CONCAT('%',#{nameCh}, '%')
AND name_en LIKE CONCAT('%',#{nameEn}, '%')
AND hs_code LIKE CONCAT('%',#{hsCode}, '%')
AND favored_f = #{favoredF}
AND affected_f = #{affectedF}
AND code_inter LIKE CONCAT('%',#{codeInter}, '%')
AND order_num = #{orderNum}
AND remark LIKE CONCAT('%',#{remark}, '%')
AND crt_by = #{crtBy}
AND crt_by_name LIKE CONCAT('%',#{crtByName}, '%')
AND crt_by_dtm = #{crtByDtm}
AND mdy_by = #{mdyBy}
AND mdy_by_name LIKE CONCAT('%',#{mdyByName}, '%')
AND mdy_by_dtm = #{mdyByDtm}
AND voided = #{voided}
AND voided_by = #{voidedBy}
AND voided_by_name LIKE CONCAT('%',#{voidedByName}, '%')
AND voided_by_dtm = #{voidedByDtm}
AND three_character_code LIKE CONCAT('%',#{threeCharacterCode}, '%')
