mapper.select:
SELECT id 'id', user_id 'userId', warehouse_id 'warehouseId', warehouse_code 'warehouseCode', warehouse_name 'warehouseName', tenant_code 'tenantCode', create_by 'createBy', create_time 'createTime', update_by 'updateBy', update_time 'updateTime', is_deleted 'isDeleted' FROM sys_user_to_warehouse WHERE is_deleted = 0
AND user_id = #{userId}
AND warehouse_id = #{warehouseId}
AND warehouse_code LIKE CONCAT('%',#{warehouseCode}, '%')
AND warehouse_name LIKE CONCAT('%',#{warehouseName}, '%')
AND tenant_code LIKE CONCAT('%',#{tenantCode}, '%')
